import osimport logginglogging.basicConfig(filename='read_replace.log',level=logging.INFO,format = '%(asctime)s %(name)s %(levelname)s %(message)s' ,filemode ='a',force=True)def read_and_replace():    flag=0    try:        with open('example.txt' , 'r') as newfile:            newfile.seek(0)            lines = newfile.readlines()            # print(lines)        loging('Reading file line by line')        for index, line in enumerate(lines):            if 'placement' in line:                flag=1                lines= line.replace('placement','screening')        if flag==1:            loging('Updating file with updated keyword- screening')            with open('example.txt' , 'w') as updated:                updated.writelines(lines)        else:            loging('No word to replace')                                         except Exception as e:        loging(e)        def create():    try:                newfile = open('example.txt' , 'w')         newfile.write("This is a placement assignment")                newfile.close()    except Exception as e:        loging(e)        def loging(e):        logging.info(e)        if __name__=="__main__":    if not os.path.exists('example.txt') :        create()            read_and_replace()                                  